history.map:
function(doc) { 
     if(doc.clientID && doc.timestamp) {
      sensor_values = [0, 0, 0];
      if (doc.temperature) {
       sensor_values[0] = doc.temperature;
     }
     if (doc.moisture) {
       sensor_values[1] = doc.moisture;
     }
     if (doc.pressure) {
       sensor_values[2] = doc.pressure;
     } 
     emit([doc.clientID, doc.timestamp], sensor_values);
     }
   }
